import { useContext } from 'react';
import { Link } from 'react-router-dom';
import AuthContext from '../context/AuthContext';

function Navbar() {
  const { user, logoutUser } = useContext(AuthContext);
  const token = localStorage.getItem('authTokens');

  let user_id;
  if (token) {
    const decoded = jwtDecode(token);
    user_id = decoded.user_id;
  }

  return (
    <div className="ms-lg-5"> {/* Added class to move everything a bit to the right */}
      <nav className="navbar navbar-expand-lg navbar-dark fixed-top bg-dark">
        <div className="container-fluid">
          <a className="navbar-brand" href="/">
            <img style={{ width: '60px', height: '60px', padding: '1px' }} src="https://salymbekov.com/wp-content/uploads/2020/12/cropped-2.png" alt="/" />
          </a>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav me-auto"> {/* Changed to me-auto to ensure content starts from the left */}
              {token === null ? (
                <>
                  <li className="nav-item">
                    <Link className="nav-link" to="/login">Login</Link>
                  </li>
                  <li className="nav-item">
                    <Link className="nav-link" to="/register">Register</Link>
                  </li>
                </>
              ) : (
                <>
                  <li className="nav-item">
                    <Link className="nav-link" to="/dashboard">Dashboard</Link>
                  </li>
                  <li className="nav-item">
                    <Link className="nav-link" to="/todo">Todo</Link>
                  </li>
                  <li className="nav-item">
                    <a className="nav-link" onClick={logoutUser} style={{ cursor: 'pointer' }}>Logout</a>
                  </li>
                </>
              )}
              <li className="nav-item ms-auto"> {/* This will push the About us to the right */}
                <Link className="nav-link" to="/aboutus">About us</Link>
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </div>
  );
}

export default Navbar;
