import { useContext } from 'react';
import { jwtDecode } from "jwt-decode"; 
import AuthContext from '../context/AuthContext';
import { Link } from 'react-router-dom';

function Navbar() {

  const { user, logoutUser } = useContext(AuthContext);
  const token = localStorage.getItem("authTokens");

  let user_id;
  if (token){
    const decoded = jwtDecode(token);
    user_id = decoded.user_id;
  }

  return (
    <div>
        <nav className="navbar navbar-expand-lg navbar-dark fixed-top bg-dark" style={{padding: '0.5rem 1rem'}}>
        <div className="container-fluid" style={{justifyContent: 'center'}}>
          <a className="navbar-brand" href="/" style={{position: 'absolute', left: '1rem'}}>
            <img style={{width: "60px", height: "60px", padding: "1px"}} src="https://salymbekov.com/wp-content/uploads/2020/12/cropped-2.png" alt="/" />
          </a>
          <div className="collapse navbar-collapse" id="navbarNav" style={{flexGrow: 0}}>
            <ul className="navbar-nav" style={{textAlign: 'center'}}>
              {token === null ? (
                <>
                  <li className="nav-item" style={{padding: '0 1rem'}}>
                    <Link className="nav-link" to="/login">Login</Link>
                  </li>
                  <li className="nav-item" style={{borderLeft: '1px solid white', padding: '0 1rem'}}>
                    <Link className="nav-link" to="/register">Register</Link>
                  </li>
                  <li className="nav-item" style={{borderLeft: '1px solid white', padding: '0 1rem'}}>
                    <Link className="nav-link" to="/aboutus">About us</Link>
                  </li>
                </>
              ) : (
                <>
                  <li className="nav-item" style={{padding: '0 1rem'}}>
                    <Link className="nav-link" to="/dashboard">Dashboard</Link>
                  </li>
                  <li className="nav-item" style={{borderLeft: '1px solid white', padding: '0 1rem'}}>
                    <Link className="nav-link" to="/todo">Todo</Link>
                  </li>
                  <li className="nav-item" style={{borderLeft: '1px solid white', padding: '0 1rem'}}>
                    <a className="nav-link" onClick={logoutUser} style={{cursor: "pointer"}}>Logout</a>
                  </li>
                </>
              )}
              
            </ul>
          </div>
        </div>
      </nav>
    </div>
  );
}

export default Navbar;
