import React, { useState } from 'react';
import './Page.css'; // Make sure the path to your CSS file is correct
import SoledatImage from '../Assets/Soledat.jpeg'; // Adjust the path as needed
import MarinaImage from '../Assets/Marina.jpeg'; // Adjust the path as needed

function AboutUs() {
  const [isUlukbekOpen, setIsUlukbekOpen] = useState(false);
  const [isMarinaOpen, setIsMarinaOpen] = useState(false);

  return (
    <div className="about-section">
      <div className="profile-card">
        <div className="name-section">
          <hr />
          <h5>Ulukbek kyzy Soledat</h5>
          <p className="position">Junior Frontend Developer</p>
          <hr />
        </div>
        <img
          src={SoledatImage}
          alt="Ulukbek kyzy Soledat"
          className="profile-pic"
          onClick={() => setIsUlukbekOpen(!isUlukbekOpen)}
        />
        {isUlukbekOpen && (
          <div className="description">
            <p>My name is Soledat, and I am a Junior Frontend Developer passionate about building responsive and accessible web applications. I specialize in React and have a keen interest in UX/UI design.</p>
          </div>
        )}
      </div>

      <div className="profile-card">
        <div className="name-section">
          <hr />
          <h5>Maksatova Marina</h5>
          <p className="position">Senior Backend Developer</p>
          <hr />
        </div>
        <img
          src={MarinaImage}
          alt="Maksatova Marina"
          className="profile-pic"
          onClick={() => setIsMarinaOpen(!isMarinaOpen)}
        />
        {isMarinaOpen && (
          <div className="description">
            <p>Hello, I'm Marina, a Senior Backend Developer with extensive experience in Node.js and database management. I love creating efficient, scalable back-end systems that empower the front-end experience.</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default AboutUs;
