import React, { useState } from 'react';
import './Page.css';
import SoledatImage from '../Assets/Soledat.jpeg';
import MarinaImage from '../Assets/Marina.jpeg';
function AboutUs() {
  const [isUlukbekOpen, setIsUlukbekOpen] = useState(false);
  const [isMarinaOpen, setIsMarinaOpen] = useState(false);

  const toggleUlukbek = () => setIsUlukbekOpen(!isUlukbekOpen);
  const toggleMarina = () => setIsMarinaOpen(!isMarinaOpen);

  return (
    <div>
           <br/><br/><br/><br/>
      <div className="about-section">
     
      <div className="profile-card">
        <img
          src={SoledatImage}
          alt="Ulukbek kyzy Soledat"
          className="profile-pic"
          onClick={toggleUlukbek}
        />
        <div className={`collapsible-content ${isUlukbekOpen ? 'open' : ''}`}>
          <h5>Ulukbek kyzy Soledat</h5>
          <p>Ulukbek is a highly talented developer known for creating innovative solutions. With a passion for technology and a keen eye for design, Ulukbek brings creativity and technical expertise to every project.</p>
        </div>
      </div>

      <div className="profile-card">
        <img
          src={MarinaImage}
          alt="Maksatova Marina"
          className="profile-pic"
          onClick={toggleMarina}
        />
        <div className={`collapsible-content ${isMarinaOpen ? 'open' : ''}`}>
          <h5>Maksatova Marina</h5>
          <p>Marina is a dedicated developer with a strong background in both front-end and back-end development. Her analytical skills and ability to solve comple.</p>
        </div>
      </div>
    </div>
    </div>
  );
}

export default AboutUs;
