import React, { useContext } from "react";
import { jwtDecode } from "jwt-decode"; 
import AuthContext from '../context/AuthContext'
import { Link } from 'react-router-dom'
import './styles/Navbar.css';

function Navbar() {
  const { logoutUser } = useContext(AuthContext)
  const token = localStorage.getItem("authTokens")
  const [showDropdown, setShowDropdown] = useState(false);

  let user_id;

  if (token) {
    const decoded = jwtDecode(token); 
    user_id = decoded.user_id;
  }

  return (
    <div className="navbar-background-style">
      <nav className="navbar">
        <Link to="/" className="navbar-logo">DOCLAB</Link>
        <div className="navbar-links"> 
          {token === null ? (
            <>
            
              <Link className="nav-link" to="/">Главная</Link>
              <Link className="nav-link" to="/">Контакты</Link>

              <div className="nav-link dropdown" 
                   onMouseEnter={() => setShowDropdown(true)} 
                   onMouseLeave={() => setShowDropdown(false)}>
                О нас
                {showDropdown && (
                  <div className="dropdown-content">
                    <Link to="/about/laboratory">О лаборатории</Link>
                    <Link to="/careers">Вакансии</Link>
                    <Link to="/reviews">Отзывы</Link>
                  </div>
                )}
              </div>


              <Link className="nav-link" to="/login">Login</Link>
              <Link className="nav-link" to="/register">Register</Link>
              <Link className="nav-link" to="/aboutus">About us</Link>
            </>
          ) : (
            <>
              <Link className="nav-link" to="/todo">To Do List</Link>
              <Link className="nav-link" onClick={logoutUser} style={{cursor: "pointer"}}>Logout</Link>
            </>
          )}
        </div>
      </nav>
    </div>
  );
}

export default Navbar;
