import React, { useState } from 'react';
import './Page.css'; // Make sure the path to your CSS file is correct
import SoledatImage from '../Assets/Soledat.jpeg'; // Adjust the path as needed
import MarinaImage from '../Assets/Marina.jpeg'; // Adjust the path as needed

function AboutUs() {
  const [isUlukbekOpen, setIsUlukbekOpen] = useState(false);
  const [isMarinaOpen, setIsMarinaOpen] = useState(false);

  return (
  <div>
    <br/><br/><br/><br/>
      <div className="about-section">
      <div className="profile-card">
        <h5>Ulukbek kyzy Soledat</h5>
        <img
          src={SoledatImage}
          alt="Ulukbek kyzy Soledat"
          className="profile-pic"
          onClick={() => setIsUlukbekOpen(!isUlukbekOpen)}
        />
        {isUlukbekOpen && (
          <div className="description">
            <p>Ulukbek is a highly talented developer known for creating innovative solutions. With a passion for technology and a keen eye for design, Ulukbek brings creativity and technical expertise to every project.</p>
          </div>
        )}
      </div>

      <div className="profile-card">
        <h5>Maksatova Marina</h5>
        <img
          src={MarinaImage}
          alt="Maksatova Marina"
          className="profile-pic"
          onClick={() => setIsMarinaOpen(!isMarinaOpen)}
        />
        {isMarinaOpen && (
          <div className="description">
            <p>Marina is a dedicated developer with a strong background in both front-end and back-end development. Her analytical skills and ability to solve complex problems make her an invaluable asset to any team.</p>
          </div>
        )}
      </div>
    </div>
  </div>
  );
}

export default AboutUs;
